INSERT INTO app.translations ("component", "location", "key", "value", "value_type", "language") VALUES ('FRONTEND', 'assignment/views/assignment-overview', 'beschrijving-hoe-een-opdracht-aan-te-maken', '<p>Maak een eerste opdracht en voeg een video- of audiofragment, een hele collectie en andere oefeningen toe. Deel daarna de opdracht met je leerlingen om zelf thuis of in de klas mee aan de slag te gaan. Daarvoor moeten ze wel toegang hebben tot Het Archief voor Onderwijs via LeerID of Smartschool. </p><p>Je kan opdrachten ook gericht delen met collega&#x27;s om samen aan te werken en/of beschikbaar maken voor al onze gebruikers op de publieke zoekpagina.</p>', 'HTML', 'nl') ON CONFLICT (component, location, key, language) DO UPDATE SET value = '<p>Maak een eerste opdracht en voeg een video- of audiofragment, een hele collectie en andere oefeningen toe. Deel daarna de opdracht met je leerlingen om zelf thuis of in de klas mee aan de slag te gaan. Daarvoor moeten ze wel toegang hebben tot Het Archief voor Onderwijs via LeerID of Smartschool. </p><p>Je kan opdrachten ook gericht delen met collega&#x27;s om samen aan te werken en/of beschikbaar maken voor al onze gebruikers op de publieke zoekpagina.</p>', value_type = 'HTML';
